import React from 'react';
import { useNavigate } from 'react-router-dom';
import axios from 'axios';
import { toast, Toaster } from 'react-hot-toast';
import { Button, TextField, Box } from '@mui/material';

const Loginpage = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();

  const handleSubmit = async (event) => {
    event.preventDefault();
    const data = new FormData(event.currentTarget);

    const userData = {
      username: data.get('username'),
      password: data.get('password'),
    };

    try {
      const response = await axios.post('http://localhost:7000/api/auth/login', userData);
      toast.success(response.data.message || "Login successful!");

      // Store username in local storage
      localStorage.setItem('username', userData.username);

      // Set login status
      setIsLoggedIn(true); // Update login status

      navigate('/home'); // Redirect to home page on successful login
    } catch (error) {
      toast.error(error.response?.data?.error || 'Login failed. Please try again.');
      console.error('There was an error!', error);
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit}>
      <TextField name="username" label="Username" required fullWidth />
      <TextField name="password" label="Password" type="password" required fullWidth />
      <Button type="submit">Login</Button>
      <Toaster />
    </Box>
  );
};

export default Loginpage;
