import React from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { AppBar, Toolbar, Typography, Avatar, Box, Button } from '@mui/material';
import { Toaster, toast } from 'react-hot-toast';
import logo from './logo.jpg';

const Navbar = ({ setIsLoggedIn }) => {
  const navigate = useNavigate();

  const handleLogout = () => {
    // Clear username from local storage
    localStorage.removeItem('username');
    setIsLoggedIn(false); // Update login status
    toast.success("Logged out successfully");
    navigate('/'); // Redirect to the login page
  };

  return (
    <AppBar position="static">
      <Toolbar>
        <img src={logo} alt="Logo" height="50" />
        <Typography variant="h6" component="div" sx={{ flexGrow: 1, ml: 2 }}>
          Employee Dashboard
        </Typography>
        {/* Navigation Links */}
        <Box sx={{ display: 'flex', alignItems: 'center', ml: 2 }}>
          <Button color="inherit" component={Link} to="/home">
            Home
          </Button>
          <Button color="inherit" component={Link} to="/dashboard">
            Dashboard
          </Button>
        </Box>
        {/* User Avatar and Username */}
        <Box sx={{ display: 'flex', alignItems: 'center', padding: '15px 23px' }}>
          <Avatar sx={{ width: 40, height: 40, mr: 1 }} alt="User Avatar" src="/path/to/avatar.jpg" /> {/* Replace with actual avatar image */}
          <Typography variant="body1" sx={{ color: 'white' }}>
            Username {/* Display username here if needed */}
          </Typography>
        </Box>
        <Button color="inherit" onClick={handleLogout}>
          Logout
        </Button>
        <Toaster />
      </Toolbar>
    </AppBar>
  );
};

export default Navbar;
