import React, { useState } from 'react';
import axios from 'axios';
import {
  Typography,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  RadioGroup,
  FormControlLabel,
  Radio,
  Button,
  Grid,
  Box,
  Avatar,
} from '@mui/material';

const EditEmployeeForm = ({ employee, onClose, onEmployeeUpdated }) => {
  const [formData, setFormData] = useState({
    name: employee.name,
    email: employee.email,
    mobile: employee.mobile,
    designation: employee.designation,
    gender: employee.gender,
    course: employee.course,
    imageUrl: employee.imageUrl || '',
  });

  const [file, setFile] = useState(null);

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleFileChange = (event) => {
    setFile(event.target.files[0]);
  };

  const uploadImage = async () => {
    if (!file) return formData.imageUrl;

    const cloudName = 'your_cloudinary_cloud_name';
    const uploadPreset = 'your_upload_preset';

    const formData = new FormData();
    formData.append('file', file);
    formData.append('upload_preset', uploadPreset);

    try {
      const response = await axios.post(
        `https://api.cloudinary.com/v1_1/${cloudName}/image/upload`,
        formData
      );
      return response.data.secure_url;
    } catch (error) {
      console.error('Error uploading image:', error);
      throw error;
    }
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      let imageUrl = formData.imageUrl;
      if (file) {
        imageUrl = await uploadImage();
      }

      const updatedData = { ...formData, imageUrl };
      await axios.put(`http://localhost:7000/api/employees/edit/${employee._id}`, updatedData);
      onEmployeeUpdated();
      onClose();
    } catch (error) {
      console.error('Error updating employee:', error);
      // Handle error (e.g., show error message to user)
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ bgcolor: 'background.paper', p: 4, borderRadius: 2 }}>
      <Typography variant="h6" gutterBottom>
        Edit Employee
      </Typography>
      <Grid container spacing={3}>
        {/* Existing form fields */}
        {/* ... */}

        <Grid item xs={12}>
          <Box display="flex" flexDirection="column" alignItems="center">
            <Avatar
              src={formData.imageUrl}
              alt={formData.name}
              sx={{ width: 100, height: 100, mb: 2 }}
            />
            <Button variant="contained" component="label">
              Update Image
              <input type="file" hidden onChange={handleFileChange} />
            </Button>
            {file && <Typography variant="body2">{file.name}</Typography>}
          </Box>
        </Grid>

        <Grid item xs={12} sm={6}>
          <Button onClick={handleSubmit} fullWidth variant="contained" color="primary">
            Update
          </Button>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Button fullWidth variant="outlined" onClick={onClose}>
            Cancel
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default EditEmployeeForm;
