import React, { useState } from 'react';
import axios from 'axios';
import {
  Typography,
  TextField,
  Select,
  MenuItem,
  FormControl,
  InputLabel,
  RadioGroup,
  FormControlLabel,
  Radio,
  Button,
  Grid,
  Box,
} from '@mui/material';

const EditEmployeeForm = ({ employee, onClose, onEmployeeUpdated }) => {
  const [formData, setFormData] = useState({
    name: employee.name,
    email: employee.email,
    mobile: employee.mobile,
    designation: employee.designation,
    gender: employee.gender,
    course: employee.course,
    // Add other fields as necessary
  });

  const handleChange = (event) => {
    const { name, value } = event.target;
    setFormData(prevState => ({
      ...prevState,
      [name]: value
    }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    try {
      await axios.put(`http://localhost:7000/api/employees/edit/${employee._id}`, formData);
      onEmployeeUpdated(); // Callback to refresh the employee list in Dashboard
      onClose(); // Close the modal
    } catch (error) {
      console.error('Error updating employee:', error);
      // Handle error (e.g., show error message to user)
    }
  };

  return (
    <Box component="form" onSubmit={handleSubmit} sx={{ bgcolor: 'background.paper', p: 4, borderRadius: 2 }}>
      <Typography variant="h6" gutterBottom>
        Edit Employee
      </Typography>
      <Grid container spacing={3}>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            fullWidth
            name="name"
            label="Name"
            value={formData.name}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            fullWidth
            name="email"
            label="Email"
            value={formData.email}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <TextField
            required
            fullWidth
            name="mobile"
            label="Mobile No"
            value={formData.mobile}
            onChange={handleChange}
          />
        </Grid>
        <Grid item xs={12} sm={6}>
          <FormControl fullWidth>
            <InputLabel>Designation</InputLabel>
            <Select
              name="designation"
              value={formData.designation}
              onChange={handleChange}
            >
              <MenuItem value="HR">HR</MenuItem>
              <MenuItem value="Manager">Manager</MenuItem>
              <MenuItem value="Sales">Sales</MenuItem>
            </Select>
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <FormControl component="fieldset">
            <Typography>Gender</Typography>
            <RadioGroup
              row
              name="gender"
              value={formData.gender}
              onChange={handleChange}
            >
              <FormControlLabel value="Male" control={<Radio />} label="Male" />
              <FormControlLabel value="Female" control={<Radio />} label="Female" />
            </RadioGroup>
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <FormControl component="fieldset">
            <Typography>Course</Typography>
            <RadioGroup
              row
              name="course"
              value={formData.course}
              onChange={handleChange}
            >
              <FormControlLabel value="MCA" control={<Radio />} label="MCA" />
              <FormControlLabel value="BCA" control={<Radio />} label="BCA" />
              <FormControlLabel value="BSC" control={<Radio />} label="BSC" />
            </RadioGroup>
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <Button variant="contained" component="label">
            Update Image
            <input type="file" hidden />
          </Button>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Button onClick={handleSubmit} fullWidth variant="contained" color="primary">
            Update
          </Button>
        </Grid>
        <Grid item xs={12} sm={6}>
          <Button fullWidth variant="outlined" onClick={onClose}>
            Cancel
          </Button>
        </Grid>
      </Grid>
    </Box>
  );
};

export default EditEmployeeForm;
