import React, { useState } from 'react';
import { 
  Table, 
  TableBody, 
  TableCell, 
  TableContainer, 
  TableHead, 
  TableRow, 
  Paper, 
  AppBar, 
  Toolbar, 
  Typography, 
  Container,
  Button,
  IconButton,
  Box,
  Fab,
  Modal,
} from '@mui/material';
import { Edit, Delete, Add } from '@mui/icons-material';
import { ThemeProvider, createTheme } from '@mui/material/styles';
import EmployeeForm from './EmployeeForm.js';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2',
    },
    secondary: {
      main: '#2196f3',
    },
  },
});

const Dashboard = () => {
  const [employees] = useState([
    { id: 1, name: 'hukum', email: 'hcgupta@cstech.in', mobile: '954010044', designation: 'HR', gender: 'Male', course: 'MCA', createDate: '13-Feb-21' },
    { id: 2, name: 'manish', email: 'manish@cstech.in', mobile: '954010033', designation: 'Sales', gender: 'Male', course: 'BCA', createDate: '12-Feb-21' },
    { id: 3, name: 'yash', email: 'yash@cstech.in', mobile: '954010022', designation: 'Manager', gender: 'Male', course: 'BSC', createDate: '11-Feb-21' },
    { id: 4, name: 'abhishek', email: 'abhishek@cstech.in', mobile: '954010033', designation: 'HR', gender: 'Male', course: 'MCA', createDate: '13-Feb-21' },
  ]);

  const [open, setOpen] = useState(false);
  const handleOpen = () => setOpen(true);
  const handleClose = () => setOpen(false);

  const handleSubmit = (event) => {
    event.preventDefault();
    // Add logic to handle form submission
    console.log('Form submitted');
    handleClose();
  };

  return (
    <ThemeProvider theme={theme}>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
            Employee Dashboard
          </Typography>
          <Button color="inherit">Logout</Button>
        </Toolbar>
      </AppBar>
      <Container maxWidth="lg" sx={{ mt: 4, mb: 4, position: 'relative', minHeight: 'calc(100vh - 64px)' }}>
        <Paper sx={{ p: 2 }}>
          <Typography variant="h5" component="h2" gutterBottom>
            Employee List
          </Typography>
          <TableContainer component={Paper}>
            <Table sx={{ minWidth: 650 }} aria-label="employee table">
              <TableHead>
                <TableRow>
                  <TableCell>Name</TableCell>
                  <TableCell>Email</TableCell>
                  <TableCell>Mobile No</TableCell>
                  <TableCell>Designation</TableCell>
                  <TableCell>Gender</TableCell>
                  <TableCell>Course</TableCell>
                  <TableCell>Create Date</TableCell>
                  <TableCell>Actions</TableCell>
                </TableRow>
              </TableHead>
              <TableBody>
                {employees.map((employee) => (
                  <TableRow key={employee.id}>
                    <TableCell>{employee.name}</TableCell>
                    <TableCell>{employee.email}</TableCell>
                    <TableCell>{employee.mobile}</TableCell>
                    <TableCell>{employee.designation}</TableCell>
                    <TableCell>{employee.gender}</TableCell>
                    <TableCell>{employee.course}</TableCell>
                    <TableCell>{employee.createDate}</TableCell>
                    <TableCell>
                      <IconButton aria-label="edit" size="small">
                        <Edit fontSize="inherit" />
                      </IconButton>
                      <IconButton aria-label="delete" size="small">
                        <Delete fontSize="inherit" />
                      </IconButton>
                    </TableCell>
                  </TableRow>
                ))}
              </TableBody>
            </Table>
          </TableContainer>
        </Paper>
        <Box sx={{ position: 'fixed', bottom: 16, right: 16 }}>
          <Fab 
            color="secondary" 
            aria-label="add" 
            onClick={handleOpen}
            sx={{ 
              width: 80, 
              height: 80, 
              boxShadow: '0 4px 8px 0 rgba(0,0,0,0.2)', 
              '&:hover': { 
                boxShadow: '0 8px 16px 0 rgba(0,0,0,0.2)',
                transform: 'scale(1.05)'
              },
              transition: 'all 0.3s ease-in-out'
            }}
          >
            <Add sx={{ fontSize: 40 }} />
          </Fab>
        </Box>
        <Modal
          open={open}
          onClose={handleClose}
          aria-labelledby="modal-modal-title"
          aria-describedby="modal-modal-description"
        >
          <Box sx={{
            position: 'absolute',
            top: '50%',
            left: '50%',
            transform: 'translate(-50%, -50%)',
            width: '100%',
            maxHeight: '90vh',
            overflowY: 'auto',
          }}>
            <EmployeeForm onSubmit={handleSubmit} onClose={handleClose} />
          </Box>
        </Modal>
      </Container>
    </ThemeProvider>
  );
};

export default Dashboard;
