import React from "react";
import { Routes, Route } from "react-router-dom";
import Login from "./components/Loginpage.js";
import Signup from "./components/Signup.js";
import Dashboard from "./components/Dashboard.js";
import Home from "./components/Home.js";
import Navbar from "./components/Navbar.js";

function App() {
  // Check if the user is logged in by checking local storage
  const isLoggedIn = !!localStorage.getItem('username'); // Change 'username' to whatever key you use for authentication

  return (
    <>
      {isLoggedIn && <Navbar />} {/* Only render Navbar if user is logged in */}
      <Routes>
        <Route path="/" element={<Login />} />
        <Route path="/signup" element={<Signup />} />
        <Route path="/home" element={<Home />} />
        <Route path="/dashboard" element={<Dashboard />} />
      </Routes>
    </>
  );
}

export default App;
